package bank;import java.io.Serializable;import java.math.BigDecimal;public class Account implements Serializable {    private static final long serialVersionUID = 1L;    private BigDecimal sum;    private String currency;    private String userName;    private double currencyValue;    public Account(BigDecimal sum, String currency, String userName, double currencyValue) {        this.sum = sum;        this.currency = currency;        this.userName = userName;        this.currencyValue = currencyValue;    }    public BigDecimal getSum() {        return sum;    }    public void setSum(BigDecimal sum) {        this.sum = sum;    }    public String getCurrency() {        return currency;    }    public void setCurrency(String currency) {        this.currency = currency;    }    public String getUserName() {        return userName;    }    public void setUserName(String userName) {        this.userName = userName;    }    public double getCurrencyValue() {        return currencyValue;    }    public void setCurrencyValue(double currencyValue) {        this.currencyValue = currencyValue;    }}