package bank.dao;import java.math.BigDecimal;import java.util.Collections;import java.util.HashSet;import java.util.Set;import bank.Account;public class InMemoryDB {    private static double currencyValue;    private static String currency;    public static Set<Account> accounts = Collections.synchronizedSet(new HashSet<>());    static {        currencyValue = 2.2;        currency = "USD";        accounts.add(new Account(new BigDecimal(100), currency, "JoeBiden", currencyValue));        accounts.add(new Account(new BigDecimal(1000), currency, "BarakObama", currencyValue));        accounts.add(new Account(new BigDecimal(1000000000), currency, "DonaldTrump", currencyValue));    }}